var threeSum = function (nums) {
    let res = []
    let len = nums.length
    if (len < 3) return res

    nums.sort((a, b) => a - b)

    for (let i = 0; i < len - 2; i++) {
      // ç¬¬ä¸€ä¸ªæ•° > 0 ä¸æˆç«‹
      if (nums[i] > 0) {
        break
      }
      // å»é‡
      if (i > 0 && nums[i] === nums[i - 1]) {
        continue
      }

      let l = i + 1
      let r = len - 1
      while (l < r) {
        let sum = nums[i] + nums[l] + nums[r]

        if (sum === 0) {
          res.push([nums[i], nums[l], nums[r]])
          // å»é‡
          while (nums[l] === nums[l + 1]) {
            l++
          }
          while (nums[r] === nums[r - 1]) {
            r--
          }
          l++
          r--
        } else if (sum < 0) {
          l++
        } else {
          r--
        }
      }
    }
    return res
}
